import React, { useState } from "react";
import styled from "styled-components";

function ReservaEspecial() {
  const [formData, setFormData] = useState({
    archivo: null,
    meses: "",
    dias: "",
    horas: "",
  });

  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validación adicional aquí si es necesario para meses, días y horas

    if (formData.archivo) {
      if (formData.archivo.type !== "application/pdf") {
        setError("El archivo debe ser un PDF.");
        return;
      } else if (formData.archivo.size > 5000000) {
        setError("El archivo no debe exceder los 5MB.");
        return;
      } else {
        setError("");
      }

      const formDataToSend = new FormData();
      formDataToSend.append("file", formData.archivo);
      formDataToSend.append("meses", formData.meses);
      formDataToSend.append("dias", formData.dias);
      formDataToSend.append("horas", formData.horas);

      try {
        const response = await fetch("http://localhost:5000/special_request", {
          method: "POST",
          body: formDataToSend,
        });

        if (response.ok) {
          const result = await response.json();
          alert("Formulario enviado con éxito: " + result.message);
        } else {
          const errorResponse = await response.json();
          setError(errorResponse.error);
        }
      } catch (err) {
        console.error("Error en la petición", err);
        setError("Ocurrió un error en la solicitud.");
      }
    } else {
      setError("Archivo es requerido.");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <FormularioContainer>
      <h2>Reserva Especial</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="archivo">Subir archivo (PDF):</label>
          <input
            type="file"
            id="archivo"
            name="archivo"
            accept=".pdf"
            onChange={(e) => {
              handleChange(e);
              setError(""); // Limpiar el error cuando se selecciona un nuevo archivo
            }}
          />
          {error && <Error>{error}</Error>}
        </div>
        <div>
          <label htmlFor="meses">Meses:</label>
          <input
            type="text"
            id="meses"
            name="meses"
            value={formData.meses}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="dias">Días específicos:</label>
          <input
            type="text"
            id="dias"
            name="dias"
            value={formData.dias}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="horas">Horas por día:</label>
          <input
            type="text"
            id="horas"
            name="horas"
            value={formData.horas}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Enviar</button>
      </form>
    </FormularioContainer>
  );
}

export default ReservaEspecial;

const FormularioContainer = styled.div`
  /* Estilos de tu contenedor */
`;

const Error = styled.span`
  /* Estilos de tu mensaje de error */
`;
