import React, { useState } from "react";
import styled from "styled-components";

function ReservaEspecial() {
  const [formData, setFormData] = useState({
    archivo: null,
    meses: "",
    dias: "",
    horaDesde: "08:00",
    horaHasta: "08:00",
  });

  const [error, setError] = useState("");

  const horas = [];
  for (let h = 8; h <= 20; h += 2) {
    const hour = h < 10 ? `0${h}:00` : `${h}:00`;
    horas.push(hour);
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validación adicional aquí si es necesario para meses, días y horas
    if (formData.horaDesde >= formData.horaHasta) {
      setError("La hora de inicio debe ser anterior a la hora de fin.");
      return;
    }

    if (formData.archivo) {
      if (formData.archivo.type !== "application/pdf") {
        setError("El archivo debe ser un PDF.");
        return;
      } else if (formData.archivo.size > 5000000) {
        setError("El archivo no debe exceder los 5MB.");
        return;
      } else {
        setError("");
      }

      const formDataToSend = new FormData();
      formDataToSend.append("file", formData.archivo);
      formDataToSend.append("meses", formData.meses);
      formDataToSend.append("dias", formData.dias);
      formDataToSend.append("horaDesde", formData.horaDesde);
      formDataToSend.append("horaHasta", formData.horaHasta);

      try {
        const response = await fetch("http://localhost:5000/special_request", {
          method: "POST",
          body: formDataToSend,
        });

        if (response.ok) {
          const result = await response.json();
          alert("Formulario enviado con éxito: " + result.message);
        } else {
          const errorResponse = await response.json();
          setError(errorResponse.error);
        }
      } catch (err) {
        console.error("Error en la petición", err);
        setError("Ocurrió un error en la solicitud.");
      }
    } else {
      setError("Archivo es requerido.");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <FormularioContainer>
      <h2>Reserva Especial</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="archivo">Subir archivo (PDF):</label>
          <input
            type="file"
            id="archivo"
            name="archivo"
            accept=".pdf"
            onChange={(e) => {
              handleChange(e);
              setError(""); // Limpiar el error cuando se selecciona un nuevo archivo
            }}
          />
          {error && <Error>{error}</Error>}
        </div>
        <div>
          <label htmlFor="meses">Meses:</label>
          <input
            type="text"
            id="meses"
            name="meses"
            value={formData.meses}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="dias">Días específicos:</label>
          <input
            type="text"
            id="dias"
            name="dias"
            value={formData.dias}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="horaDesde">Desde:</label>
          <select
            id="horaDesde"
            name="horaDesde"
            value={formData.horaDesde}
            onChange={handleChange}
          >
            {horas.map((hora) => (
              <option key={hora} value={hora}>
                {hora}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor="horaHasta">Hasta:</label>
          <select
            id="horaHasta"
            name="horaHasta"
            value={formData.horaHasta}
            onChange={handleChange}
          >
            {horas.map((hora) => (
              <option key={hora} value={hora}>
                {hora}
              </option>
            ))}
          </select>
        </div>
        {error && <Error>{error}</Error>}
        <button type="submit">Enviar</button>
      </form>
    </FormularioContainer>
  );
}

export default ReservaEspecial;

const FormularioContainer = styled.div`
  /* Estilos de tu contenedor */
`;

const Error = styled.span`
  /* Estilos de tu mensaje de error */
`;
