import React, { useState } from 'react';
import { useForm } from "react-hook-form";
import { Link, useNavigate } from 'react-router-dom';
import "../Estilos/Register.css";

const Formulario = () => {
    const { register, formState: { errors }, handleSubmit, watch } = useForm();
    const navigate = useNavigate();
    const [mostrarContraseña, setMostrarContraseña] = useState(false);
    const [mostrarRepContraseña, setMostrarRepContraseña] = useState(false);

    const onSubmit = async (data) => {
        try {
            const response = await fetch('http://localhost:5000/users', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    username: data.nombres + ' ' + data.apellidos, 
                    rut: data.rut,
                    password: data.contraseña,
                    email: data.correo
                })
            });
    
            if (response.ok) {
                const result = await response.json();
                alert("Usuario registrado:", result);
                // Aquí asumo que el backend devuelve un token en result.token al registrar el usuario
                // Redirige al inicio después del registro exitoso
                navigate('/Login'); 
            } else if (response.status === 409) {
                const errorData = await response.json();
                console.error("Error:", errorData);
                alert("Error: " + errorData); // Muestra un mensaje de usuario ya registrado
            } else {
                const errorData = await response.json();
                console.error("Error:", errorData);
                alert("Error en el registro: " + errorData); // Muestra un mensaje de error general
            }
        } catch (error) {
            console.error("Error en la conexión:", error);
            alert("No se pudo conectar al servidor. Verifica tu conexión o intenta más tarde.");
        }
    };
    

    
    const contraseña = watch("contraseña");

    return (
        <div id="register-body">
            <form id="formulario-registro" onSubmit={handleSubmit(onSubmit)}>
                <h2 id="titulo-registro">Registro</h2>
                
                {/* Campo Nombres */}
                <div id="campo-nombres">
                    <label htmlFor="nombres">Nombres</label>
                    <input
                        id="input-nombres"
                        type="text"
                        {...register('nombres', {
                            required: "El campo nombres es requerido",
                            maxLength: { value: 30, message: "Máximo 30 caracteres" }
                        })}
                    />
                    {errors.nombres && <p id="error-nombres" className="error-message" data-error-id="nombres">{errors.nombres.message}</p>}
                </div>

                {/* Campo Apellidos */}
                <div id="campo-apellidos">
                    <label htmlFor="apellidos">Apellidos</label>
                    <input
                        id="input-apellidos"
                        type="text"
                        {...register('apellidos', {
                            required: "El campo apellidos es requerido",
                            maxLength: { value: 30, message: "Máximo 30 caracteres" }
                        })}
                    />
                    {errors.apellidos && <p id="error-apellidos" className="error-message" data-error-id="apellidos">{errors.apellidos.message}</p>}
                </div>

                {/* Campo RUT */}
                <div id="campo-rut">
                    <label htmlFor="rut">Rut</label>
                    <input
                        id="input-rut"
                        type="text"
                        {...register('rut', {
                            required: "El campo RUT es requerido",
                            pattern: {
                                value: /^[0-9]+-[0-9kK]{1}$/,
                                message: 'El formato del RUT es incorrecto. Ej: 12345678-9'
                            }
                        })}
                    />
                    {errors.rut && <p id="error-rut" className="error-message" data-error-id="rut">{errors.rut.message}</p>}
                </div>

                {/* Campo Correo */}
                <div id="campo-correo">
                    <label htmlFor="correo">Correo</label>
                    <input
                        id="input-correo"
                        type="text"
                        {...register('correo', {
                            required: "El campo correo es requerido",
                            pattern: {
                                value: /^[a-zA-Z0-9._%+-]+@(alu\.uct\.cl|uct\.cl)$/,
                                message: 'El correo debe terminar en @alu.uct.cl o @uct.cl'
                            }
                        })}
                    />
                    {errors.correo && <p id="error-correo" className="error-message" data-error-id="correo">{errors.correo.message}</p>}
                </div>

                {/* Campo Contraseña */}
                <div id="campo-contraseña">
                    <label htmlFor="contraseña">Contraseña</label>
                    <div id="password-input-container">
                        <input
                            id="input-contraseña"
                            type={mostrarContraseña ? "text" : "password"}
                            {...register('contraseña', {
                                required: "El campo contraseña es requerido",
                            })}
                        />
                        <i 
                            id="password-iconn" 
                            className={`fas ${mostrarContraseña ? 'fa-eye-slash' : 'fa-eye'}`}
                            onClick={() => setMostrarContraseña(!mostrarContraseña)} 
                        />
                    </div>
                    {errors.contraseña && <p id="error-contraseña" className="error-message" data-error-id="contraseña">{errors.contraseña.message}</p>}
                </div>

                <div id="campo-repcontraseña">
                    <label htmlFor="repcontraseña">Repetir Contraseña</label>
                    <div id="password-input-container">
                        <input
                            id="input-repcontraseña"
                            type={mostrarRepContraseña ? "text" : "password"}
                            {...register('repcontraseña', {
                                required: "El campo repetir contraseña es requerido",
                                validate: value => value === contraseña || "Las contraseñas no coinciden"
                            })}
                        />
                        <i 
                            id="rep-password-icon" 
                            className={`fas ${mostrarRepContraseña ? 'fa-eye-slash' : 'fa-eye'}`}
                            onClick={() => setMostrarRepContraseña(!mostrarRepContraseña)} 
                        />
                    </div>
                    {errors.repcontraseña && <p id="error-repcontraseña" className="error-message" data-error-id="repcontraseña">{errors.repcontraseña.message}</p>}
                </div>

                <input id="boton-registrarse" type="submit" value="Registrarse" /> 

                <p id="redirect-message">       
                    ¿Ya tienes una cuenta? <Link to="/Login">Inicia Sesión</Link>
                </p>
            </form>
        </div>
    );
}

export default Formulario;
