import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es'; 
registerLocale('es', es); 

function ReservayEquipo() {
  const navigate = useNavigate(); 
  const obtenerProximaFechaHabil = () => {
    const hoy = new Date();
    const dia = hoy.getDay(); 

    if (dia === 6) { 
      hoy.setDate(hoy.getDate() + 2); 
    } else if (dia === 0) { 
      hoy.setDate(hoy.getDate() + 1); 
    }

    return hoy;
  };

  const calcularFechaMaxima = (fechaMinima) => {
    const fechaMax = new Date(fechaMinima);
  
    const mesActual = fechaMinima.getMonth();
    const semestreActual = Math.floor(mesActual / 6) + 1;
  
    if (semestreActual === 1) {
      fechaMax.setFullYear(fechaMinima.getFullYear(), 5, 30); 
    } else {
      fechaMax.setFullYear(fechaMinima.getFullYear(), 11, 31); 
    }
  
    const dia = fechaMax.getDay();
    if (dia === 6) { // Sábado
      fechaMax.setDate(fechaMax.getDate() - 1); 
    } else if (dia === 0) { // Domingo
      fechaMax.setDate(fechaMax.getDate() - 2); 
    }
  
    return fechaMax;
  };
  

  const fechaMinima = obtenerProximaFechaHabil();
  const fechaMaxima = calcularFechaMaxima(fechaMinima);

  const [disp, setDis] = useState(null);
  const [selectedDate, setSelectedDate] = useState(fechaMinima);
  const [time, setTime] = useState(null);
  const [error, setError] = useState(null);
  const [cancha, setCancha] = useState('');
  const [canchaTipo, setCanchaTipo] = useState('');
  const [equipo, setEquipo] = useState('');
  const [canchasDisponibles, setCanchasDisponibles] = useState([]);
  const [equiposDisponibles, setEquiposDisponibles] = useState([]);
  const [horasSinDisponibilidad, setHorasSinDisponibilidad] = useState([]); // Nuevo estado


  useEffect(() => {
    const fetchCanchasYEquipos = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No se encontró el token de autenticación.');
        return;
      }

      try {
        const response = await fetch('http://localhost:5000/api/equipo_and_canchas', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
          body: JSON.stringify({ 
            fecha: selectedDate.toISOString().split('T')[0], 
            hora: time 
          }),
        });

        if (response.ok) {
          const data = await response.json();
          setCanchasDisponibles(data.canchas_disponibles);
          setEquiposDisponibles(data.equipos_disponibles);
          
          // Actualizar las horas sin disponibilidad
          const horasSinDisponibilidadTemp = generarOpcionesTiempo().filter(hora => {
            return !data.canchas_disponibles.some(c => c.horas_no_disponibles.includes(hora));
          });
          setHorasSinDisponibilidad(horasSinDisponibilidadTemp);
        } else {
          const errorData = await response.json();
          setError(errorData.error || 'Error al obtener los datos.');
        }
      } catch (error) {
        console.error('Error de red:', error);
        setError('Error al conectar con el servidor.');
      }
    };

    if (selectedDate && time) {
      fetchCanchasYEquipos();
    }
  }, [selectedDate, time]);


  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);

    const token = localStorage.getItem('token');
    const formData = {
      fecha: selectedDate.toISOString().split('T')[0], // Formato YYYY-MM-DD
      hora: time,
      cancha: cancha,
      equipo: equipo,
    };

    try {
      const response = await fetch('http://localhost:5000/api/reservas', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify(formData),
      });

      if (response.status === 201) {
        alert("Se ha reservado con éxito");
        navigate("/TuReservacion"); 
      } else if (response.status === 409) {
        const result = await response.json();
        setError(result.error); 
      } else {
        setError('Ocurrió un error al realizar la reserva.');
      }
    } catch (error) {
      setError('Error al conectar con el servidor.');
    }
  };

  useEffect(() => {
    const verificarReservas = async () => {
      setError(null);
      const token = localStorage.getItem('token');
      const formData = {
        fecha: selectedDate.toISOString().split('T')[0], 
      };

      try {
        const response = await fetch('http://localhost:5000/api/verificar_reservas', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
          body: JSON.stringify(formData),
        });

        if (response.ok) {
          setDis(true);
        } else if (response.status === 409) {
          const result = await response.json();
          setDis(false);
          setError(result.error);
        } else if (response.status === 410) {
          const result = await response.json();
          setDis(false);
          alert(result.error);
          navigate("/TuReservacion"); 
        }

      } catch (error) {
        setError('Error al conectar con el servidor.');
      }
    };

    if (selectedDate) {
      verificarReservas();
    }
  }, [selectedDate, navigate]); 

  const generarOpcionesTiempo = () => {
    const opciones = [];
    for (let hora = 8; hora <= 18; hora += 2) {
      const horaFormateada = hora.toString().padStart(2, '0') + ':00';
      const hayCanchaDisponible = canchasDisponibles.some(c => c.horas_disponibles.includes(horaFormateada)); // Verifica si hay canchas disponibles en esa hora
      opciones.push({ hora: horaFormateada, disponible: hayCanchaDisponible });
    }
    return opciones;
  };

  const esHoraPasada = (horaSeleccionada) => {
    const hoy = new Date();
    const fechaSeleccionada = selectedDate.toDateString() === hoy.toDateString();

    if (!fechaSeleccionada) return false;

    const [hora, minuto] = horaSeleccionada.split(':').map(Number);
    const ahora = new Date();
    return hora < ahora.getHours() || (hora === ahora.getHours() && minuto < ahora.getMinutes());
  };

  const handleCanchaChange = (e) => {
    const canchaNombre = e.target.value;
    setCancha(canchaNombre);

    const canchaSeleccionada = canchasDisponibles.find(c => c.nombre === canchaNombre);
    if (canchaSeleccionada) {
      setCanchaTipo(canchaSeleccionada.tipo);
    } else {
      setCanchaTipo('');
    }
  };

  const equiposFiltrados = canchaTipo
    ? equiposDisponibles.filter(equipo => equipo.tipo === canchaTipo)
    : equiposDisponibles;

  return (
    <Wrapper>
      <FormularioContainer>
        <h2>Reserva tu Hora y Equipo</h2>
        <form onSubmit={handleSubmit}>
          <Fecha>
            <Label>Selecciona el Día:</Label>
              <DatePickerWrapper>
                <DatePicker
                  selected={selectedDate}
                  onChange={(date) => setSelectedDate(date)}
                  inline
                  minDate={fechaMinima} 
                  maxDate={fechaMaxima} 
                  filterDate={(date) => {
                    const day = date.getDay();
                    return day !== 0 && day !== 6; 
                  }}
                  dateFormat="P" 
                  locale="es" 
                  required
                  showMonthDropdown={false}
                  showYearDropdown={false}
                  dropdownMode="select"
                />
              </DatePickerWrapper>
          </Fecha>
          <Hora>
            <Label htmlFor="hora">Selecciona la Hora:</Label>
            <Select
              id="hora"
              name="hora"
              value={time}
              onChange={(e) => setTime(e.target.value)}
              disabled={!disp}
              required
            >
              <option value="">Selecciona una hora</option>
              {generarOpcionesTiempo().map(({ hora, disponible }) => (
                <option 
                  key={hora} 
                  value={hora} 
                  disabled={!disponible} 
                  style={!disponible ? { color: 'red' } : {}} // Cambia el color solo si no hay canchas
                >
                  {hora} {!disponible && '(No hay canchas disponibles)'}
                </option>
              ))}
            </Select>
          </Hora>
          <Cancha>
            <Label htmlFor="cancha">Selecciona la Cancha:</Label>
            <Select
              id="cancha"
              name="cancha"
              value={cancha}
              onChange={handleCanchaChange}
              required
              disabled={!time} 
            >
              <option value="">Selecciona una cancha</option>
              {canchasDisponibles.map((c) => (
                <option key={c._id} value={c.nombre}>{c.nombre}</option>
              ))}
            </Select>
          </Cancha>
          <Equipo>
            <Label htmlFor="equipo">Selecciona tu Equipo:</Label>
            <Select
              id="equipo"
              name="equipo"
              value={equipo}
              onChange={(e) => setEquipo(e.target.value)}
              required
              disabled={!time || !canchaTipo} 
            >
              <option value="">Selecciona un equipo</option>
              {equiposFiltrados.map((e) => (
                <option key={e._id} value={e.nombre}>{e.nombre}</option>
              ))}
            </Select>
          </Equipo>
          <Button type="submit">Reservar</Button>
        </form>
        {error && <ErrorNotification>{error}</ErrorNotification>}
      </FormularioContainer>
    </Wrapper>
  );
}

