import React, { useState, useEffect } from "react";
import styled from "styled-components";
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es'; 

function ReservaEspecial() {
  const [formData, setFormData] = useState({
    archivo: null,
    meses: {
      Enero: false,
      Febrero: false,
      Marzo: false,
      Abril: false,
      Mayo: false,
      Junio: false,
      Julio: false,
      Agosto: false,
      Septiembre: false,
      Octubre: false,
      Noviembre: false,
      Diciembre: false,
    },
    dias: {
      Lunes: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: "", equipo: "" },
      Martes: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: "", equipo: "" },
      Miércoles: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: "", equipo: "" },
      Jueves: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: "", equipo: "" },
      Viernes: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: "", equipo: "" },
    },
  });

  const [selectedDate, setSelectedDate] = useState(null);
  const [mesesDisponibles, setMesesDisponibles] = useState([]);
  const [diaActual] = useState(new Date());
  const [reservarDiaEspecifico, setReservarDiaEspecifico] = useState(false);
  const [maxDate, setMaxDate] = useState(new Date());
  const [horaDesdeSeleccionada, setHoraDesdeSeleccionada] = useState("10:00");
  const [cancha, setCancha] = useState('');
  const [canchaTipo, setCanchaTipo] = useState('');
  const [equipo, setEquipo] = useState('');
  const [canchasDisponibles, setCanchasDisponibles] = useState([]);
  const [equiposDisponibles, setEquiposDisponibles] = useState([]);

  const horas = [];
  for (let h = 8; h <= 20; h += 2) {
    const hour = h < 10 ? `0${h}:00` : `${h}:00`;
    horas.push(hour);
  }

  useEffect(() => {
    const mesActual = diaActual.getMonth();
    const semestreActual = Math.floor(mesActual / 6) + 1;

    const meses = semestreActual === 1
      ? ["Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio"]
      : ["Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre"];

    const mesesFiltrados = meses.slice(mesActual % 6);
    const mesesSeleccionables = mesesFiltrados.map(mes => ({
      nombre: mes,
      seleccionado: false,
    }));

    setMesesDisponibles(mesesSeleccionables);

    const finalSemestre = semestreActual === 1 
      ? new Date(diaActual.getFullYear(), 6, 0)
      : new Date(diaActual.getFullYear(), 12, 0);

    setMaxDate(finalSemestre);
  }, [diaActual]);

  const handleMonthChange = (mes) => {
    setFormData((prevState) => ({
      ...prevState,
      meses: {
        ...prevState.meses,
        [mes]: !prevState.meses[mes],
      },
    }));
  };

  const handleHourChange = (dia, tipo, valor) => {
    setFormData((prevState) => {
      const newHoras = {
        ...prevState.dias[dia],
        [tipo]: valor,
      };
      return {
        ...prevState,
        dias: {
          ...prevState.dias,
          [dia]: newHoras,
        },
      };
    });

    if (tipo === "horaDesde") {
      setHoraDesdeSeleccionada(valor);
    }
  };

  const handleCheckboxChange = (dia) => {
    setFormData((prevState) => ({
      ...prevState,
      dias: {
        ...prevState.dias,
        [dia]: {
          ...prevState.dias[dia],
          seleccionado: !prevState.dias[dia].seleccionado,
        },
      },
    }));
  };

  const fetchCanchasYEquipos = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No se encontró el token de autenticación.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/equipo_and_canchas', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ 
          fecha: selectedDate.toISOString().split('T')[0], 
          hora: time 
        }),
      });

      if (response.ok) {
        const data = await response.json();
        setCanchasDisponibles(data.canchas_disponibles);
        setEquiposDisponibles(data.equipos_disponibles);
      } else {
        const errorData = await response.json();
        console.error(errorData.error || 'Error al obtener los datos.');
      }
    } catch (error) {
      console.error('Error de red:', error);
    }
  };

  if (selectedDate) {
    fetchCanchasYEquipos();
  } [selectedDate];

  const handleCanchaChange = (dia, canchaNombre) => {
    setFormData((prevState) => ({
      ...prevState,
      dias: {
        ...prevState.dias,
        [dia]: {
          ...prevState.dias[dia],
          cancha: canchaNombre,
        },
      },
    }));
  };

  const handleEquipoChange = (dia, equipoNombre) => {
    setFormData((prevState) => ({
      ...prevState,
      dias: {
        ...prevState.dias,
        [dia]: {
          ...prevState.dias[dia],
          equipo: equipoNombre,
        },
      },
    }));
  };

  const toggleReservarDiaEspecifico = () => {
    setReservarDiaEspecifico(!reservarDiaEspecifico);
    setSelectedDate(null);
  };

  const equiposFiltrados = canchaTipo
    ? equiposDisponibles.filter(equipo => equipo.tipo === canchaTipo)
    : equiposDisponibles;

  const handleSubmit = async (event) => {
    event.preventDefault();
    const bloquesDeHoras = Object.keys(formData.dias)
      .filter(dia => formData.dias[dia].seleccionado)
      .map(dia => ({
        dia,
        horaDesde: formData.dias[dia].horaDesde,
        horaHasta: formData.dias[dia].horaHasta,
        cancha: formData.dias[dia].cancha,
        equipo: formData.dias[dia].equipo,
      }));

    const solicitudReservas = {
      bloquesDeHoras,
      meses: formData.meses,
      estado: 'Pendiente',
    };

    // Enviar la solicitud al servidor...
  };

  return (
    <FormularioContainer>
      <h2>Reserva Especial</h2>
      <form onSubmit={handleSubmit}>
        <FormGroup>
          <h3>Seleccione cómo desea reservar:</h3>
          <LabelRadio>
            <InputRadio
              type="radio"
              checked={!reservarDiaEspecifico}
              onChange={() => setReservarDiaEspecifico(false)}
            />
            Días en general
          </LabelRadio>
          <LabelRadio>
            <InputRadio
              type="radio"
              checked={reservarDiaEspecifico}
              onChange={toggleReservarDiaEspecifico}
            />
            Día específico
          </LabelRadio>
        </FormGroup>

        {!reservarDiaEspecifico && (
          <>
            <h3>Seleccione los días, horas, cancha y equipo:</h3>
            {Object.keys(formData.dias).map((dia) => (
              <div key={dia}>
                <LabelCheckbox>
                  <InputCheckbox
                    type="checkbox"
                    checked={formData.dias[dia].seleccionado}
                    onChange={() => handleCheckboxChange(dia)}
                  />
                  {dia}
                </LabelCheckbox>

                {formData.dias[dia].seleccionado && (
                  <FormGroup>
                    <Label htmlFor={`horaDesde-${dia}`}>Desde:</Label>
                    <Select
                      id={`horaDesde-${dia}`}
                      value={formData.dias[dia].horaDesde}
                      onChange={(e) => handleHourChange(dia, "horaDesde", e.target.value)}
                    >
                      {horas.slice(0, -1).map((hora) => (
                        <option key={hora} value={hora}>{hora}</option>
                      ))}
                    </Select>

                    <Label htmlFor={`horaHasta-${dia}`}>Hasta:</Label>
                    <Select
                      id={`horaHasta-${dia}`}
                      value={formData.dias[dia].horaHasta}
                      onChange={(e) => handleHourChange(dia, "horaHasta", e.target.value)}
                    >
                      {horas.filter(hora => hora > formData.dias[dia].horaDesde).map((hora) => (
                        <option key={hora} value={hora}>{hora}</option>
                      ))}
                    </Select>

                    <Label htmlFor={`cancha-${dia}`}>Cancha:</Label>
                    <Select
                      id={`cancha-${dia}`}
                      value={formData.dias[dia].cancha}
                      onChange={(e) => handleCanchaChange(dia, e.target.value)}
                    >
                      {canchasDisponibles.map((c) => (
                        <option key={c._id} value={c.nombre}>{c.nombre}</option>
                      ))}
                    </Select>

                    <Label htmlFor={`equipo-${dia}`}>Equipo:</Label>
                    <Select
                      id={`equipo-${dia}`}
                      value={formData.dias[dia].equipo}
                      onChange={(e) => handleEquipoChange(dia, e.target.value)}
                    >
                      {equiposFiltrados.map((e) => (
                        <option key={e._id} value={e.nombre}>{e.nombre}</option>
                      ))}
                    </Select>
                  </FormGroup>
                )}
              </div>
            ))}
          </>
        )}

        {reservarDiaEspecifico && (
          <FormGroup>
            <Label htmlFor="date-picker">Seleccione una fecha:</Label>
            <DatePicker
              id="date-picker"
              selected={selectedDate}
              onChange={(date) => setSelectedDate(date)}
              minDate={diaActual}
              maxDate={maxDate}
              dateFormat="dd/MM/yyyy"
              locale={es}
            />
          </FormGroup>
        )}

        <FormGroup>
          <h3>Seleccione los meses para la reserva:</h3>
          {mesesDisponibles.map((mes) => (
            <LabelCheckbox key={mes.nombre}>
              <InputCheckbox
                type="checkbox"
                checked={formData.meses[mes.nombre]}
                onChange={() => handleMonthChange(mes.nombre)}
              />
              {mes.nombre}
            </LabelCheckbox>
          ))}
        </FormGroup>

        <Boton type="submit">Solicitar Reserva</Boton>
      </form>
    </FormularioContainer>
  );
}

