import React, { useState, useEffect } from "react";
import styled from "styled-components";
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function ReservaEspecial() {
  const [formData, setFormData] = useState({
    archivo: null,
    meses: {
      Enero: false,
      Febrero: false,
      Marzo: false,
      Abril: false,
      Mayo: false,
      Junio: false,
      Julio: false,
      Agosto: false,
      Septiembre: false,
      Octubre: false,
      Noviembre: false,
      Diciembre: false,
    },
    dias: {
      Lunes: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: '', equipo: '' },
      Martes: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: '', equipo: '' },
      Miércoles: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: '', equipo: '' },
      Jueves: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: '', equipo: '' },
      Viernes: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: '', equipo: '' },
    },
  });

  const [selectedDate, setSelectedDate] = useState(null);
  const [error, setError] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);
  const [mesesDisponibles, setMesesDisponibles] = useState([]);
  const [diaActual] = useState(new Date());
  const [reservarDiaEspecifico, setReservarDiaEspecifico] = useState(false);
  const [maxDate, setMaxDate] = useState(new Date());
  const [horaDesdeSeleccionada, setHoraDesdeSeleccionada] = useState("10:00"); 
  const [cancha, setCancha] = useState('');
  const [canchaTipo, setCanchaTipo] = useState('');
  const [equiposDisponibles, setEquiposDisponibles] = useState([]);
  const [canchasDisponibles, setCanchasDisponibles] = useState([]);

  const horas = [];
  for (let h = 8; h <= 20; h += 2) {
    const hour = h < 10 ? `0${h}:00` : `${h}:00`;
    horas.push(hour);
  }

  const fetchCanchasYEquipos = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No se encontró el token de autenticación.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/canchas_equipo', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setCanchasDisponibles(data.canchas_disponibles);
        setEquiposDisponibles(data.equipos_disponibles);
      } else {
        const errorData = await response.json();
        setError(errorData.error || 'Error al obtener los datos.');
      }
    } catch (error) {
      console.error('Error de red:', error);
      setError('Error al conectar con el servidor.');
    }
  };

  useEffect(() => {
    fetchCanchasYEquipos();
  }, []);

  useEffect(() => {
    const mesActual = diaActual.getMonth();
    const semestreActual = Math.floor(mesActual / 6) + 1;

    const meses = semestreActual === 1
      ? ["Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio"]
      : ["Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre"];

    const mesesFiltrados = meses.slice(mesActual % 6);
    const mesesSeleccionables = mesesFiltrados.map(mes => ({
      nombre: mes,
      seleccionado: false,
    }));

    setMesesDisponibles(mesesSeleccionables);

    const finalSemestre = semestreActual === 1 
      ? new Date(diaActual.getFullYear(), 6, 0) 
      : new Date(diaActual.getFullYear(), 12, 0);

    setMaxDate(finalSemestre);
  }, [diaActual]);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
    setError("");
  };

  const handleHourChange = (dia, tipo, valor) => {
    setFormData((prevState) => {
      const newHoras = {
        ...prevState.dias[dia],
        [tipo]: valor,
      };

      if (tipo === "horaHasta" && convertToMinutes(newHoras.horaHasta) <= convertToMinutes(newHoras.horaDesde)) {
        return prevState; 
      }

      return {
        ...prevState,
        dias: {
          ...prevState.dias,
          [dia]: newHoras,
        },
      };
    });

    if (tipo === "horaDesde") {
      setHoraDesdeSeleccionada(valor);
    }
  };

  const horasHastaDisponibles = horas.filter(hora => convertToMinutes(hora) > convertToMinutes(horaDesdeSeleccionada));

  const handleCheckboxChange = (dia) => {
    setFormData((prevState) => ({
      ...prevState,
      dias: {
        ...prevState.dias,
        [dia]: {
          ...prevState.dias[dia],
          seleccionado: !prevState.dias[dia].seleccionado,
        },
      },
    }));
  };

  const handleMonthChange = (mes) => {
    setFormData((prevState) => ({
      ...prevState,
      meses: {
        ...prevState.meses,
        [mes]: !prevState.meses[mes],
      },
    }));
  };

  const toggleReservarDiaEspecifico = () => {
    setReservarDiaEspecifico(!reservarDiaEspecifico);
    setSelectedDate(null);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const mesesSeleccionados = Object.keys(formData.meses).filter(mes => formData.meses[mes]);
    const reservas = crearReservas(mesesSeleccionados);

    if (reservas.length === 0) {
      setError("No se han seleccionado días ni horas.");
      return;
    }

    console.log(reservas);
  };

  const crearReservas = (meses) => {
    const reservas = [];

    meses.forEach((mes) => {
      const year = new Date().getFullYear(); 
      const mesIndex = new Date().getMonth(); 
      const primerDia = new Date(year, mesIndex, 1); 
      const ultimoDia = new Date(year, mesIndex + 1, 0);

      for (let dia = primerDia; dia <= ultimoDia; dia.setDate(dia.getDate() + 1)) {
        const diaNombre = dia.toLocaleString('es-ES', { weekday: 'long' });

        Object.keys(formData.dias).forEach((key) => {
          const diaData = formData.dias[key];

          if (diaData.seleccionado && diaNombre === key) {
            const horaDesde = new Date(`${dia.toISOString().split('T')[0]}T${diaData.horaDesde}:00`);
            const horaHasta = new Date(`${dia.toISOString().split('T')[0]}T${diaData.horaHasta}:00`);

            for (let hora = horaDesde; hora < horaHasta; hora.setHours(hora.getHours() + 2)) {
              const reserva = {
                fecha: dia.toISOString().split('T')[0],
                horaDesde: hora.toTimeString().split(' ')[0].slice(0, 5),
                horaHasta: new Date(hora).setHours(hora.getHours() + 2).toTimeString().split(' ')[0].slice(0, 5),
                cancha: diaData.cancha,
                equipo: diaData.equipo,
                id: generarIdUnico(),
              };
              reservas.push(reserva);
            }
          }
        });
      }
    });
    return reservas;
  };

  const generarIdUnico = () => {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, (c) => {
      const r = Math.random() * 16 | 0;
      const v = c === 'x' ? r : (r & 0x3 | 0x8);
      return v.toString(16);
    });
  };

  const handleCanchaChange = (dia, e) => {
    const canchaNombre = e.target.value;
    setFormData((prevState) => ({
      ...prevState,
      dias: {
        ...prevState.dias,
        [dia]: {
          ...prevState.dias[dia],
          cancha: canchaNombre,
        },
      },
    }));
    setCancha(canchaNombre);
  };

  return (
    <Container>
      <h2>Formulario de Reserva Especial</h2>
      <form onSubmit={handleSubmit}>
        <input type="file" accept="application/pdf" onChange={handleFileChange} />
        
        {reservarDiaEspecifico && (
          <div>
            <label>Seleccionar día específico:</label>
            <DatePicker
              selected={selectedDate}
              onChange={(date) => setSelectedDate(date)}
              minDate={diaActual}
              maxDate={maxDate}
              dateFormat="dd/MM/yyyy"
              placeholderText="Seleccionar día"
            />
          </div>
        )}

        {!reservarDiaEspecifico && (
          <div>
            <h3>Seleccionar por mes</h3>
            <div>
              {mesesDisponibles.map((mes) => (
                <label key={mes.nombre}>
                  <input
                    type="checkbox"
                    checked={mes.seleccionado}
                    onChange={() => handleMonthChange(mes.nombre)}
                  />
                  {mes.nombre}
                </label>
              ))}
            </div>

            <h3>Seleccionar días de la semana y horas</h3>
            {Object.keys(formData.dias).map((dia) => (
              <div key={dia}>
                <label>
                  <input
                    type="checkbox"
                    checked={formData.dias[dia].seleccionado}
                    onChange={() => handleCheckboxChange(dia)}
                  />
                  {dia}
                </label>

                {formData.dias[dia].seleccionado && (
                  <div>
                    <label>Hora desde:</label>
                    <select
                      value={formData.dias[dia].horaDesde}
                      onChange={(e) => handleHourChange(dia, "horaDesde", e.target.value)}
                    >
                      {horas.map((hora) => (
                        <option key={hora} value={hora}>
                          {hora}
                        </option>
                      ))}
                    </select>

                    <label>Hora hasta:</label>
                    <select
                      value={formData.dias[dia].horaHasta}
                      onChange={(e) => handleHourChange(dia, "horaHasta", e.target.value)}
                    >
                      {horasHastaDisponibles.map((hora) => (
                        <option key={hora} value={hora}>
                          {hora}
                        </option>
                      ))}
                    </select>

                    <label>Cancha:</label>
                    <select value={formData.dias[dia].cancha} onChange={(e) => handleCanchaChange(dia, e)}>
                      <option value="">Seleccionar cancha</option>
                      {canchasDisponibles.map((cancha) => (
                        <option key={cancha} value={cancha}>{cancha}</option>
                      ))}
                    </select>

                    <label>Equipo:</label>
                    <select value={formData.dias[dia].equipo} onChange={(e) => handleCanchaChange(dia, e)}>
                      <option value="">Seleccionar equipo</option>
                      {equiposDisponibles.map((equipo) => (
                        <option key={equipo} value={equipo}>{equipo}</option>
                      ))}
                    </select>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        <div>
          <button type="button" onClick={toggleReservarDiaEspecifico}>
            {reservarDiaEspecifico ? "Cambiar a selección por mes" : "Cambiar a selección de día específico"}
          </button>
          <button type="submit">Reservar</button>
        </div>
      </form>
    </Container>
  );
}

export default ReservaEspecial;

const Container = styled.div`
  padding: 20px;
`;

function convertToMinutes(time) {
  const [hours, minutes] = time.split(':');
  return parseInt(hours) * 60 + parseInt(minutes);
}
