import React, { useState, useEffect } from "react";
import styled from "styled-components";
import DatePicker from "react-datepicker"; // Asegúrate de instalar react-datepicker
import "react-datepicker/dist/react-datepicker.css"; // Estilo de DatePicker

function ReservaEspecial() {
  const [formData, setFormData] = useState({
    archivo: null,
    meses: {
      Enero: false,
      Febrero: false,
      Marzo: false,
      Abril: false,
      Mayo: false,
      Junio: false,
      Julio: false,
      Agosto: false,
      Septiembre: false,
      Octubre: false,
      Noviembre: false,
      Diciembre: false,
    },
    dias: {
      Lunes: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
      Martes: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
      Miércoles: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
      Jueves: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
      Viernes: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
    },
  });

  const [selectedDate, setSelectedDate] = useState(null);
  const [error, setError] = useState("");
  const [mesesDisponibles, setMesesDisponibles] = useState([]);
  const [diaActual] = useState(new Date());
  const [reservarDiaEspecifico, setReservarDiaEspecifico] = useState(false);

  const horas = [];
  for (let h = 8; h <= 20; h += 2) {
    const hour = h < 10 ? `0${h}:00` : `${h}:00`;
    horas.push(hour);
  }

  useEffect(() => {
    const mesActual = diaActual.getMonth(); // 0-11
    const semestreActual = Math.floor(mesActual / 6) + 1; // 1 o 2

    const meses = semestreActual === 1
      ? ["Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio"]
      : ["Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre"];

    // Filtrar meses para mostrar desde el mes actual hasta el final del semestre
    const mesesFiltrados = meses.slice(mesActual % 6);
    const mesesSeleccionables = mesesFiltrados.map(mes => ({
      nombre: mes,
      seleccionado: false,
    }));

    setMesesDisponibles(mesesSeleccionables);
  }, [diaActual]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { dias, meses } = formData;

    // Validar que todas las horas "Desde" sean anteriores a las horas "Hasta"
    if (reservarDiaEspecifico) {
      if (selectedDate) {
        const dia = selectedDate.toLocaleString("es-ES", { weekday: "long" }); // Obtener el día de la semana
        if (dias[dia].horaDesde >= dias[dia].horaHasta) {
          setError(`La hora de inicio para ${dia} debe ser anterior a la hora de fin.`);
          return;
        }
      }
    } else {
      for (const dia in dias) {
        if (dias[dia].seleccionado) {
          if (dias[dia].horaDesde >= dias[dia].horaHasta) {
            setError(`La hora de inicio para ${dia} debe ser anterior a la hora de fin.`);
            return;
          }
        }
      }
    }

    if (formData.archivo) {
      if (formData.archivo.type !== "application/pdf") {
        setError("El archivo debe ser un PDF.");
        return;
      } else if (formData.archivo.size > 5000000) {
        setError("El archivo no debe exceder los 5MB.");
        return;
      } else {
        setError("");
      }

      const formDataToSend = new FormData();
      formDataToSend.append("file", formData.archivo);
      formDataToSend.append("meses", JSON.stringify(meses)); // Convertir a JSON para enviar
      formDataToSend.append("dias", JSON.stringify(dias)); // Convertir a JSON para enviar
      if (reservarDiaEspecifico && selectedDate) {
        formDataToSend.append("fechaEspecifica", selectedDate.toISOString());
      }

      try {
        const response = await fetch("http://localhost:5000/special_request", {
          method: "POST",
          body: formDataToSend,
        });

        if (response.ok) {
          const result = await response.json();
          alert("Formulario enviado con éxito: " + result.message);
        } else {
          const errorResponse = await response.json();
          setError(errorResponse.error);
        }
      } catch (err) {
        console.error("Error en la petición", err);
        setError("Ocurrió un error en la solicitud.");
      }
    } else {
      setError("Archivo es requerido.");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleHourChange = (dia, tipo, valor) => {
    setFormData((prevState) => ({
      ...prevState,
      dias: {
        ...prevState.dias,
        [dia]: {
          ...prevState.dias[dia],
          [tipo]: valor,
        },
      },
    }));
  };

  const handleCheckboxChange = (dia) => {
    setFormData((prevState) => ({
      ...prevState,
      dias: {
        ...prevState.dias,
        [dia]: {
          ...prevState.dias[dia],
          seleccionado: !prevState.dias[dia].seleccionado,
        },
      },
    }));
  };

  const handleMonthChange = (mes) => {
    setFormData((prevState) => ({
      ...prevState,
      meses: {
        ...prevState.meses,
        [mes]: !prevState.meses[mes], // Alternar selección del mes
      },
    }));
  };

  const toggleReservarDiaEspecifico = () => {
    setReservarDiaEspecifico(!reservarDiaEspecifico);
    setSelectedDate(null); // Limpiar la fecha seleccionada al cambiar la opción
  };

  return (
    <FormularioContainer>
      <h2>Reserva Especial</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="archivo">Subir archivo (PDF):</label>
          <input
            type="file"
            id="archivo"
            name="archivo"
            accept=".pdf"
            onChange={(e) => {
              handleChange(e);
              setError(""); // Limpiar el error cuando se selecciona un nuevo archivo
            }}
          />
          {error && <Error>{error}</Error>}
        </div>

        <div>
          <h3>Seleccione cómo desea reservar:</h3>
          <label>
            <input
              type="radio"
              checked={!reservarDiaEspecifico}
              onChange={() => setReservarDiaEspecifico(false)}
            />
            Días en general
          </label>
          <label>
            <input
              type="radio"
              checked={reservarDiaEspecifico}
              onChange={toggleReservarDiaEspecifico}
            />
            Día específico
          </label>
        </div>

        {reservarDiaEspecifico && (
          <>
            <Fecha>
              <Label>Selecciona el Día:</Label>
              <DatePickerWrapper>
                <DatePicker
                  selected={selectedDate}
                  onChange={(date) => {
                    const dia = date.toLocaleString("es-ES", { weekday: "long" });
                    
                    // Si el día no existe en formData.dias, inicialízalo con valores por defecto
                    if (!formData.dias[dia]) {
                      setFormData((prevState) => ({
                        ...prevState,
                        dias: {
                          ...prevState.dias,
                          [dia]: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: true }
                        },
                      }));
                    }

                    setSelectedDate(date); // Establece la fecha seleccionada
                  }}
                  inline
                  minDate={diaActual}
                  filterDate={(date) => {
                    const day = date.getDay();
                    return day !== 0 && day !== 6; // Solo lunes a viernes
                  }}
                  dateFormat="P"
                  locale="es"
                  required
                  showMonthDropdown={false}
                  showYearDropdown={false}
                  dropdownMode="select"
                />

              </DatePickerWrapper>
            </Fecha>

            <div>
              <h3>Seleccione las horas para el día específico:</h3>
              <label htmlFor="horaDesde">Desde:</label>
              <select
                id="horaDesde"
                value={formData.dias[selectedDate ? selectedDate.toLocaleString("es-ES", { weekday: "long" }) : "Lunes"].horaDesde}
                onChange={(e) => handleHourChange(selectedDate.toLocaleString("es-ES", { weekday: "long" }), "horaDesde", e.target.value)}
              >
                {horas.map((hora) => (
                  <option key={hora} value={hora}>
                    {hora}
                  </option>
                ))}
              </select>
              <label htmlFor="horaHasta">Hasta:</label>
              <select
                id="horaHasta"
                value={formData.dias[selectedDate ? selectedDate.toLocaleString("es-ES", { weekday: "long" }) : "Lunes"].horaHasta}
                onChange={(e) => handleHourChange(selectedDate.toLocaleString("es-ES", { weekday: "long" }), "horaHasta", e.target.value)}
              >
                {horas.map((hora) => (
                  <option key={hora} value={hora}>
                    {hora}
                  </option>
                ))}
              </select>
            </div>
          </>
        )}

        {!reservarDiaEspecifico && (
          <>
            <div>
              <h3>Seleccione los meses:</h3>
              {mesesDisponibles.map(({ nombre, seleccionado }) => (
                <div key={nombre}>
                  <label>
                    <input
                      type="checkbox"
                      checked={formData.meses[nombre]}
                      onChange={() => handleMonthChange(nombre)}
                    />
                    {nombre}
                  </label>
                </div>
              ))}
            </div>

            <div>
              <h3>Seleccione los días:</h3>
              {Object.keys(formData.dias).map((dia) => (
                <div key={dia}>
                  <label>
                    <input
                      type="checkbox"
                      checked={formData.dias[dia].seleccionado}
                      onChange={() => handleCheckboxChange(dia)}
                    />
                    {dia}
                  </label>
                  {formData.dias[dia].seleccionado && (
                    <div>
                      <label htmlFor={`horaDesde-${dia}`}>Desde:</label>
                      <select
                        id={`horaDesde-${dia}`}
                        value={formData.dias[dia].horaDesde}
                        onChange={(e) => handleHourChange(dia, "horaDesde", e.target.value)}
                      >
                        {horas.map((hora) => (
                          <option key={hora} value={hora}>
                            {hora}
                          </option>
                        ))}
                      </select>
                      <label htmlFor={`horaHasta-${dia}`}>Hasta:</label>
                      <select
                        id={`horaHasta-${dia}`}
                        value={formData.dias[dia].horaHasta}
                        onChange={(e) => handleHourChange(dia, "horaHasta", e.target.value)}
                      >
                        {horas.map((hora) => (
                          <option key={hora} value={hora}>
                            {hora}
                          </option>
                        ))}
                      </select>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </>
        )}

        <button type="submit">Enviar Reserva</button>
      </form>
    </FormularioContainer>
  );
}

// Estilos
const FormularioContainer = styled.div`
  // Agrega estilos aquí según sea necesario
`;

const Fecha = styled.div`
  margin: 20px 0;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 10px;
`;

const DatePickerWrapper = styled.div`
  display: inline-block;
  margin-bottom: 10px;
`;

const Error = styled.div`
  color: red;
  margin-top: 10px;
`;

export default ReservaEspecial;
