import React, { useState } from 'react';
import styled from 'styled-components';

const FormularioContainer = styled.div`
  background-color: #f0f4f8;
  padding: 30px;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  max-width: 600px;
  margin: 20px auto;
`;

const Titulo = styled.h2`
  font-size: 24px;
  color: #2c3e50;
  text-align: center;
  margin-bottom: 20px;
`;

const Subtitulo = styled.h3`
  font-size: 20px;
  color: #34495e;
  margin-bottom: 15px;
`;

const InputArchivo = styled.input`
  display: block;
  margin-top: 10px;
  margin-bottom: 20px;
  padding: 8px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 5px;
  width: 100%;
  box-sizing: border-box;
`;

const Label = styled.label`
  font-weight: bold;
  color: #34495e;
`;

const RadioButton = styled.label`
  display: block;
  margin: 10px 0;
  font-size: 16px;
  color: #2c3e50;
`;

const Checkbox = styled.input`
  margin-right: 10px;
`;

const BotonEnviar = styled.button`
  background-color: #3498db;
  color: white;
  padding: 10px 20px;
  font-size: 16px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  width: 100%;
  box-sizing: border-box;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #2980b9;
  }
`;

const SelectHoras = styled.select`
  padding: 8px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 5px;
  margin: 10px 0;
  width: 100%;
`;

const ContenedorDias = styled.div`
  margin-top: 20px;
`;

const Error = styled.div`
  color: red;
  margin-top: 10px;
  text-align: center;
`;

const FormularioRegistro = () => {
  const [archivo, setArchivo] = useState(null);
  const [diaGeneral, setDiaGeneral] = useState('');
  const [diaEspecifico, setDiaEspecifico] = useState('');
  const [horaGeneral, setHoraGeneral] = useState('');
  const [horaEspecifica, setHoraEspecifica] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!archivo || !diaGeneral || !horaGeneral || !diaEspecifico || !horaEspecifica) {
      setError('Por favor, completa todos los campos.');
      return;
    }

    // Aquí se puede manejar el envío del formulario
    console.log('Formulario enviado con éxito');
    setError('');
  };

  return (
    <FormularioContainer>
      <Titulo>Formulario de Reserva</Titulo>

      <form onSubmit={handleSubmit}>
        <Subtitulo>Subir archivo (PDF)</Subtitulo>
        <InputArchivo
          type="file"
          accept=".pdf"
          onChange={(e) => setArchivo(e.target.files[0])}
        />

        <Subtitulo>Seleccionar día en general</Subtitulo>
        <Label htmlFor="diaGeneral">Día general (por mes):</Label>
        <SelectHoras
          id="diaGeneral"
          value={diaGeneral}
          onChange={(e) => setDiaGeneral(e.target.value)}
        >
          <option value="">Selecciona un día</option>
          <option value="dia1">Día 1</option>
          <option value="dia2">Día 2</option>
          <option value="dia3">Día 3</option>
        </SelectHoras>

        <Label htmlFor="horaGeneral">Hora general (por mes):</Label>
        <SelectHoras
          id="horaGeneral"
          value={horaGeneral}
          onChange={(e) => setHoraGeneral(e.target.value)}
        >
          <option value="">Selecciona una hora</option>
          <option value="8-10">8:00 - 10:00</option>
          <option value="10-12">10:00 - 12:00</option>
        </SelectHoras>

        <Subtitulo>Seleccionar día específico</Subtitulo>
        <Label htmlFor="diaEspecifico">Día específico:</Label>
        <SelectHoras
          id="diaEspecifico"
          value={diaEspecifico}
          onChange={(e) => setDiaEspecifico(e.target.value)}
        >
          <option value="">Selecciona un día</option>
          <option value="dia4">Día 4</option>
          <option value="dia5">Día 5</option>
          <option value="dia6">Día 6</option>
        </SelectHoras>

        <Label htmlFor="horaEspecifica">Hora específica:</Label>
        <SelectHoras
          id="horaEspecifica"
          value={horaEspecifica}
          onChange={(e) => setHoraEspecifica(e.target.value)}
        >
          <option value="">Selecciona una hora</option>
          <option value="12-14">12:00 - 14:00</option>
          <option value="14-16">14:00 - 16:00</option>
        </SelectHoras>

        {error && <Error>{error}</Error>}

        <BotonEnviar type="submit">Enviar</BotonEnviar>
      </form>
    </FormularioContainer>
  );
};

export default FormularioRegistro;
