import React, { useState, useEffect } from "react";
import styled from "styled-components";
import DatePicker from "react-datepicker"; 
import "react-datepicker/dist/react-datepicker.css"; 

function ReservaEspecial() {
  const [formData, setFormData] = useState({
    archivo: null,
    meses: {
      Enero: false,
      Febrero: false,
      Marzo: false,
      Abril: false,
      Mayo: false,
      Junio: false,
      Julio: false,
      Agosto: false,
      Septiembre: false,
      Octubre: false,
      Noviembre: false,
      Diciembre: false,
    },
    dias: {
      Lunes: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
      Martes: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
      Miércoles: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
      Jueves: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
      Viernes: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
    },
  });

  const [selectedDate, setSelectedDate] = useState(null);
  const [error, setError] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);
  const [mesesDisponibles, setMesesDisponibles] = useState([]);
  const [diaActual] = useState(new Date());
  const [reservarDiaEspecifico, setReservarDiaEspecifico] = useState(false);
  const [horaDesde, setHoraDesde] = useState("08:00");
  const [horaHasta, setHoraHasta] = useState("08:00");

  const horas = [];
  for (let h = 8; h <= 20; h += 2) {
    const hour = h < 10 ? `0${h}:00` : `${h}:00`;
    horas.push(hour);
  }

  useEffect(() => {
    const mesActual = diaActual.getMonth(); 
    const semestreActual = Math.floor(mesActual / 6) + 1; 

    const meses = semestreActual === 1
      ? ["Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio"]
      : ["Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre"];

    const mesesFiltrados = meses.slice(mesActual % 6);
    const mesesSeleccionables = mesesFiltrados.map(mes => ({
      nombre: mes,
      seleccionado: false,
    }));

    setMesesDisponibles(mesesSeleccionables);
  }, [diaActual]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
    setError(""); 
  };

  const handleHourChange = (dia, tipo, valor) => {
    setFormData((prevState) => ({
      ...prevState,
      dias: {
        ...prevState.dias,
        [dia]: {
          ...prevState.dias[dia],
          [tipo]: valor,
        },
      },
    }));
  };

  const handleCheckboxChange = (dia) => {
    setFormData((prevState) => ({
      ...prevState,
      dias: {
        ...prevState.dias,
        [dia]: {
          ...prevState.dias[dia],
          seleccionado: !prevState.dias[dia].seleccionado,
        },
      },
    }));
  };

  const handleMonthChange = (mes) => {
    setFormData((prevState) => ({
      ...prevState,
      meses: {
        ...prevState.meses,
        [mes]: !prevState.meses[mes], 
      },
    }));
  };

  const toggleReservarDiaEspecifico = () => {
    setReservarDiaEspecifico(!reservarDiaEspecifico);
    setSelectedDate(null); 
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);

    const token = localStorage.getItem('token');
    const data = new FormData();
    
    // JSON para reserva específica
    if (reservarDiaEspecifico) {
      const fecha = selectedDate ? selectedDate.toISOString().split('T')[0] : null; 
      const bloquesDeHoras = [{ dia: fecha, horaDesde, horaHasta }]; 

      const solicitudReservas = {
        fecha,
        bloquesDeHoras,
        estado: 'Pendiente',
      };

      data.append('solicitudReservas', JSON.stringify(solicitudReservas));
    } 
    // JSON para reserva general
    else {
      const mesesSeleccionados = Object.keys(formData.meses).filter(mes => formData.meses[mes]);
      const diasSeleccionados = Object.keys(formData.dias).filter(dia => formData.dias[dia].seleccionado);

      const solicitudReservas = {
        meses: mesesSeleccionados,
        dias: diasSeleccionados.map(dia => ({
          nombre: dia,
          horaDesde: formData.dias[dia].horaDesde,
          horaHasta: formData.dias[dia].horaHasta,
        })),
        estado: 'Pendiente',
      };

      data.append('solicitudReservas', JSON.stringify(solicitudReservas));
    }

    // Si hay un archivo, adjuntarlo
    if (selectedFile) {
      data.append('file', selectedFile);
    }

    try {
      const response = await fetch('http://localhost:5000/special_request', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: data,
      });

      if (response.ok) {
        alert("Tu solicitud ha sido enviada para su revisión.");
      } else {
        const result = await response.json();
        setError(result.error || 'Error al enviar la solicitud.');
      }
    } catch (error) {
      setError('Error al conectar con el servidor.');
    }
  };

  return (
    <FormularioContainer>
      <h2>Reserva Especial</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="archivo">Subir archivo (PDF):</label>
          <input
            type="file"
            id="archivo"
            name="archivo"
            accept=".pdf"
            onChange={handleFileChange}
          />
          {error && <Error>{error}</Error>}
        </div>

        <div>
          <h3>Seleccione cómo desea reservar:</h3>
          <label>
            <input
              type="radio"
              checked={!reservarDiaEspecifico}
              onChange={() => setReservarDiaEspecifico(false)}
            />
            Días en general
          </label>
          <label>
            <input
              type="radio"
              checked={reservarDiaEspecifico}
              onChange={toggleReservarDiaEspecifico}
            />
            Día específico
          </label>
        </div>

        {reservarDiaEspecifico && (
          <Fecha>
            <Label>Selecciona el Día:</Label>
            <DatePickerWrapper>
              <DatePicker
                selected={selectedDate}
                onChange={(date) => {
                  setSelectedDate(date);
                }}
                inline
                minDate={diaActual}
                filterDate={(date) => date.getDay() !== 0 && date.getDay() !== 6}
                dateFormat="P"
                locale="es"
                required
              />
            </DatePickerWrapper>

            <div>
              <Label>Selecciona el Horario:</Label>
              <div>
                <label htmlFor="horaDesde">Desde:</label>
                <select
                  id="horaDesde"
                  value={horaDesde}
                  onChange={(e) => setHoraDesde(e.target.value)}
                >
                  {horas.map((hora) => (
                    <option key={hora} value={hora}>
                      {hora}
                    </option>
                  ))}
                </select>

                <label htmlFor="horaHasta">Hasta:</label>
                <select
                  id="horaHasta"
                  value={horaHasta}
                  onChange={(e) => setHoraHasta(e.target.value)}
                >
                  {horas.map((hora) => (
                    <option key={hora} value={hora}>
                      {hora}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </Fecha>
        )}

        {!reservarDiaEspecifico && (
          <>
            <div>
              <h3>Seleccione los meses:</h3>
              {mesesDisponibles.map(({ nombre }) => (
                <div key={nombre}>
                  <label>
                    <input
                      type="checkbox"
                      checked={formData.meses[nombre]}
                      onChange={() => handleMonthChange(nombre)}
                    />
                    {nombre}
                  </label>
                </div>
              ))}
            </div>

            <div>
              <h3>Seleccione los días:</h3>
              {Object.keys(formData.dias).map((dia) => (
                <div key={dia}>
                  <label>
                    <input
                      type="checkbox"
                      checked={formData.dias[dia].seleccionado}
                      onChange={() => handleCheckboxChange(dia)}
                    />
                    {dia}
                  </label>

                  {formData.dias[dia].seleccionado && (
                    <div>
                      <label htmlFor={`horaDesde-${dia}`}>Desde:</label>
                      <select
                        id={`horaDesde-${dia}`}
                        value={formData.dias[dia].horaDesde}
                        onChange={(e) => handleHourChange(dia, "horaDesde", e.target.value)}
                      >
                        {horas.map((hora) => (
                          <option key={hora} value={hora}>
                            {hora}
                          </option>
                        ))}
                      </select>

                      <label htmlFor={`horaHasta-${dia}`}>Hasta:</label>
                      <select
                        id={`horaHasta-${dia}`}
                        value={formData.dias[dia].horaHasta}
                        onChange={(e) => handleHourChange(dia, "horaHasta", e.target.value)}
                      >
                        {horas.map((hora) => (
                          <option key={hora} value={hora}>
                            {hora}
                          </option>
                        ))}
                      </select>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </>
        )}

        <Boton type="submit">Enviar Solicitud</Boton>
      </form>
    </FormularioContainer>
  );
}

