import React, { useState, useEffect } from "react";
import styled from "styled-components";
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css'; 

function ReservaEspecial() {
  const [formData, setFormData] = useState({
    archivo: null,
    meses: {
      Enero: false,
      Febrero: false,
      Marzo: false,
      Abril: false,
      Mayo: false,
      Junio: false,
      Julio: false,
      Agosto: false,
      Septiembre: false,
      Octubre: false,
      Noviembre: false,
      Diciembre: false,
    },
    dias: {
      Lunes: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: '', equipo: '' },
      Martes: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: '', equipo: '' },
      Miércoles: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: '', equipo: '' },
      Jueves: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: '', equipo: '' },
      Viernes: { horaDesde: "08:00", horaHasta: "10:00", seleccionado: false, cancha: '', equipo: '' },
    },
  });

  const [selectedDate, setSelectedDate] = useState(new Date());
  const [error, setError] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);
  const [mesesDisponibles, setMesesDisponibles] = useState([]);
  const [diaActual] = useState(new Date());
  const [reservarDiaEspecifico, setReservarDiaEspecifico] = useState(false);
  const [maxDate, setMaxDate] = useState(new Date());
  const [horaDesdeSeleccionada, setHoraDesdeSeleccionada] = useState("10:00"); 
  const [cancha, setCancha] = useState('');
  const [canchaTipo, setCanchaTipo] = useState('');
  const [equipo, setEquipo] = useState('');
  const [canchasDisponibles, setCanchasDisponibles] = useState([]);
  const [equiposDisponibles, setEquiposDisponibles] = useState([]);

  const horas = [];
  for (let h = 8; h <= 20; h += 2) {
    const hour = h < 10 ? `0${h}:00` : `${h}:00`;
    horas.push(hour);
  }

  
  const fetchCanchasYEquipos = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No se encontró el token de autenticación.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/canchas_equipo', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setCanchasDisponibles(data.canchas_disponibles);
        setEquiposDisponibles(data.equipos_disponibles)
      } else {
        const errorData = await response.json();
        setError(errorData.error || 'Error al obtener los datos.');
      }
    } catch (error) {
      console.error('Error de red:', error);
      setError('Error al conectar con el servidor.');
    }
  };

  useEffect(() => {
    fetchCanchasYEquipos();
  }, []);

  useEffect(() => {
    const mesActual = diaActual.getMonth();
    const semestreActual = Math.floor(mesActual / 6) + 1;

    const meses = semestreActual === 1
      ? ["Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio"]
      : ["Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre"];

    const mesesFiltrados = meses.slice(mesActual % 6);
    const mesesSeleccionables = mesesFiltrados.map(mes => ({
      nombre: mes,
      seleccionado: false,
    }));

    setMesesDisponibles(mesesSeleccionables);

    // Calcular la fecha máxima
    const finalSemestre = semestreActual === 1 
      ? new Date(diaActual.getFullYear(), 6, 0) // Último día de Junio
      : new Date(diaActual.getFullYear(), 12, 0); // Último día de Diciembre

    setMaxDate(finalSemestre);
  }, [diaActual]);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
    setError("");
  };
  const convertToMinutes = (time) => {
    const [hours, minutes] = time.split(':').map(Number);
    return hours * 60 + minutes;
  };

  const handleHourChange = (dia, tipo, valor) => {
    setFormData((prevState) => {
      const newHoras = {
        ...prevState.dias[dia],
        [tipo]: valor,
      };

      if (tipo === "horaHasta" && convertToMinutes(newHoras.horaHasta) <= convertToMinutes(newHoras.horaDesde)) {
        return prevState; 
      }

      return {
        ...prevState,
        dias: {
          ...prevState.dias,
          [dia]: newHoras,
        },
      };
    });

    if (tipo === "horaDesde") {
      setHoraDesdeSeleccionada(valor);
    }
  };

  const horasHastaDisponibles = horas.filter(hora => convertToMinutes(hora) > convertToMinutes(horaDesdeSeleccionada));


  const handleCheckboxChange = (dia) => {
    setFormData((prevState) => ({
      ...prevState,
      dias: {
        ...prevState.dias,
        [dia]: {
          ...prevState.dias[dia],
          seleccionado: !prevState.dias[dia].seleccionado,
        },
      },
    }));
  };

  const handleMonthChange = (mes) => {
    setFormData((prevState) => ({
      ...prevState,
      meses: {
        ...prevState.meses,
        [mes]: !prevState.meses[mes],
      },
    }));
  };

  const toggleReservarDiaEspecifico = () => {
    setReservarDiaEspecifico(!reservarDiaEspecifico);
    setSelectedDate(null);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);
  
    const token = localStorage.getItem('token');
    const fecha = selectedDate ? selectedDate.toISOString().split('T')[0] : null;
  
    // Obtener el mes de octubre a partir de la fecha seleccionada
    const mesSeleccionado = selectedDate.getMonth(); // Mes de 0 a 11
    const añoSeleccionado = selectedDate.getFullYear();
  
    // Generar un ID único para las reservas especiales
    const idReservaEspecial = generarIdUnico();
  
    // Obtener los bloques de reservas
    const bloquesDeHoras = Object.keys(formData.dias)
      .filter(dia => formData.dias[dia].seleccionado)
      .flatMap(dia => crearReservas(dia, formData.dias[dia].horaDesde, formData.dias[dia].horaHasta, mesSeleccionado, añoSeleccionado, idReservaEspecial));
  
    const solicitudReservas = {
      reservas: bloquesDeHoras,
      meses: formData.meses,
      estado: 'Pendiente',
      email_usuario: formData.email_usuario, // Asegúrate de tener este dato en formData
      cancha: formData.cancha, // Asegúrate de tener este dato en formData
      equipo: formData.equipo, // Asegúrate de tener este dato en formData
      id_reserva_especial: idReservaEspecial, // ID único para este conjunto
    };
  
    const data = new FormData();
    data.append('solicitudReservas', JSON.stringify(solicitudReservas));
    if (selectedFile) {
      data.append('file', selectedFile);
    }
  
    try {
      const response = await fetch('http://localhost:5000/special_request', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: data,
      });
  
      if (response.ok) {
        alert("Tu solicitud ha sido enviada para su revisión.");
      } else {
        const result = await response.json();
        setError(result.error || 'Error al enviar la solicitud.');
      }
    } catch (error) {
      setError('Error al conectar con el servidor.');
    }
  };
  
  // Función para crear las reservas basadas en el día seleccionado
  const crearReservas = (dia, horaDesde, horaHasta, mes, año, idReservaEspecial) => {
    const reservas = [];
    const horasDesde = new Date();
    horasDesde.setHours(...horaDesde.split(':').map(Number));
  
    const horasHasta = new Date(horasDesde);
    horasHasta.setHours(horasHasta.getHours() + 2); // Duración de 2 horas
  
    const diasMap = {
      lunes: 1,
      martes: 2,
      miércoles: 3,
      jueves: 4,
      viernes: 5,
    };
  
    // Calcular el primer día del mes para obtener las reservas
    const primerDiaDelMes = new Date(año, mes, 1);
    const ultimoDiaDelMes = new Date(año, mes + 1, 0); // Último día del mes
  
    // Iterar sobre cada día del mes
    for (let d = primerDiaDelMes; d <= ultimoDiaDelMes; d.setDate(d.getDate() + 1)) {
      const diaSemana = d.getDay();
      const diaString = Object.keys(diasMap).find(key => diasMap[key] === diaSemana);
  
      // Si el día de la semana está seleccionado, se crea una reserva
      if (diaString === dia) {
        reservas.push({
          fecha: d.toISOString().split('T')[0],
          hora: horasDesde.toISOString().split('T')[1].split(':').slice(0, 2).join(':'),
          cancha: formData.cancha,
          equipo: formData.equipo,
          email_usuario: formData.email_usuario,
          id_reserva_especial: idReservaEspecial,
        });
  
        // Crear una reserva adicional cada 2 horas
        if (horasHasta.getHours() > horasDesde.getHours() + 2) {
          horasDesde.setHours(horasDesde.getHours() + 2);
          reservas.push({
            fecha: d.toISOString().split('T')[0],
            hora: horasDesde.toISOString().split('T')[1].split(':').slice(0, 2).join(':'),
            cancha: formData.cancha,
            equipo: formData.equipo,
            email_usuario: formData.email_usuario,
            id_reserva_especial: idReservaEspecial,
          });
        }
      }
    }
  
    return reservas;
  };
  
  // Función para generar un ID único
  const generarIdUnico = () => {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, (c) => {
      const r = Math.random() * 16 | 0;
      const v = c === 'x' ? r : (r & 0x3 | 0x8);
      return v.toString(16);
    });
  };
  

  const handleCanchaChange = (dia, e) => { 
    const canchaNombre = e.target.value;
  
    setFormData((prevState) => {
      const diaData = prevState.dias[dia];
      const canchaSeleccionada = canchasDisponibles.find(c => c.nombre === canchaNombre);
      
      const nuevoEquipo = canchaSeleccionada ? '' : diaData.equipo;
  
      return {
        ...prevState,
        dias: {
          ...prevState.dias,
          [dia]: {
            ...diaData,
            cancha: canchaNombre,
            equipo: nuevoEquipo, 
          },
        },
      };
    });
  
    // Actualiza el tipo de cancha
    const canchaSeleccionada = canchasDisponibles.find(c => c.nombre === canchaNombre);
    if (canchaSeleccionada) {
      setCanchaTipo(canchaSeleccionada.tipo);
    } else {
      setCanchaTipo('');
    }
  };

  const handleCanchaChange_2 = (e) => {
    const canchaNombre = e.target.value;
    setCancha(canchaNombre);

    const canchaSeleccionada = canchasDisponibles.find(c => c.nombre === canchaNombre);
    if (canchaSeleccionada) {
      setCanchaTipo(canchaSeleccionada.tipo);
    } else {
      setCanchaTipo('');
    }
  };
  
  const handleEquipoChange = (dia, e) => {
    const equipoSeleccionado = e.target.value;
  
    setFormData((prevState) => {
      const diaData = prevState.dias[dia];
      
      return {
        ...prevState,
        dias: {
          ...prevState.dias,
          [dia]: {
            ...diaData,
            equipo: equipoSeleccionado,
          },
        },
      };
    });
  };
  
  const equiposFiltrados = canchaTipo
    ? equiposDisponibles.filter(equipo => equipo.tipo === canchaTipo)
    : equiposDisponibles;
  
  return (
    <FormularioContainer>
      <h2>Reserva Especial</h2>
      <form onSubmit={handleSubmit}>
        <FormGroup>
          <Label htmlFor="archivo">Subir archivo (PDF):</Label>
          <Input
            type="file"
            id="archivo"
            name="archivo"
            accept=".pdf"
            onChange={handleFileChange}
          />
          {error && <Error>{error}</Error>}
        </FormGroup>

        <FormGroup>
          <h3>Seleccione cómo desea reservar:</h3>
          <LabelRadio>
            <InputRadio
              type="radio"
              checked={!reservarDiaEspecifico}
              onChange={() => setReservarDiaEspecifico(false)}
            />
            Días en general
          </LabelRadio>
          <LabelRadio>
            <InputRadio
              type="radio"
              checked={reservarDiaEspecifico}
              onChange={toggleReservarDiaEspecifico}
            />
            Día específico
          </LabelRadio>
        </FormGroup>

        {reservarDiaEspecifico && (
          <>
            <Fecha>
              <Label>Selecciona el Día:</Label>
              <DatePickerWrapper>
                <DatePicker
                  selected={selectedDate}
                  onChange={(date) => {setSelectedDate(date);}}
                  inline
                  minDate={diaActual}
                  maxDate={maxDate}
                  filterDate={(date) => date.getDay() !== 0 && date.getDay() !== 6}
                  dateFormat="P"
                  locale="es"
                  required
                />
              </DatePickerWrapper>
            </Fecha>
            <h3>Seleccione las horas:</h3>
            <FormGroup>
              <Label htmlFor="horaDesde">Desde:</Label>
              <Select
                id="horaDesde"
                value={formData.dias[selectedDate ? selectedDate.toLocaleDateString() : "Lunes"]?.horaDesde || ''} // Usar el día seleccionado
                onChange={(e) => handleHourChange(selectedDate.toLocaleDateString(), "horaDesde", e.target.value)}
              >
                {horas.slice(0, -1).map((hora) => (
                  <option key={hora} value={hora}>
                    {hora}
                  </option>
                ))}
              </Select>

              <Label htmlFor="horaHasta">Hasta:</Label>
              <Select
                id="horaHasta"
                value={formData.dias[selectedDate ? selectedDate.toLocaleDateString() : "Lunes"]?.horaHasta || ''}
                onChange={(e) => handleHourChange(selectedDate.toLocaleDateString(), "horaHasta", e.target.value)}
              >
                {horasHastaDisponibles.map((hora) => (
                  <option key={hora} value={hora}>
                    {hora}
                  </option>
                ))}
              </Select>

              <Label htmlFor="cancha">Selecciona la Cancha:</Label>
              <Select 
                id="cancha"
                name="cancha"
                value={cancha}
                onChange={(e) => handleCanchaChange_2(e)}
                required
              >
                <option value="">Selecciona una cancha</option>
                {canchasDisponibles.map((c) => (
                  <option key={c._id} value={c.nombre}>{c.nombre}</option>
                ))}
              </Select>

              <Label htmlFor="equipo">Selecciona tu Equipo:</Label>
              <Select
                id="equipo"
                name="equipo"
                value={equipo}
                onChange={(e) => setEquipo(e.target.value)}
                required
                disabled={!canchaTipo} 
              >
                <option value="">Selecciona un equipo</option>
                {equiposFiltrados.map((e) => (
                  <option key={e._id} value={e.nombre}>{e.nombre}</option>
                ))}
              </Select>
            </FormGroup>
          </>
        )}

        {!reservarDiaEspecifico && (
          <>
            <FormGroup>
              <h3>Seleccione los meses:</h3>
              {mesesDisponibles.map(({ nombre }) => (
                <LabelCheckbox key={nombre}>
                  <InputCheckbox
                    type="checkbox"
                    checked={formData.meses[nombre]}
                    onChange={() => handleMonthChange(nombre)}
                  />
                  {nombre}
                </LabelCheckbox>
              ))}
            </FormGroup>

            <FormGroup>
              <h3>Seleccione los días y horas para cada mes:</h3>
              {Object.keys(formData.dias).map((dia) => (
                <div key={dia}>
                  <LabelCheckbox>
                    <InputCheckbox
                      type="checkbox"
                      checked={formData.dias[dia].seleccionado}
                      onChange={() => handleCheckboxChange(dia)}
                    />
                    {dia}
                  </LabelCheckbox>

                  {formData.dias[dia].seleccionado && (
                    <FormGroup>
                      <Label htmlFor={`horaDesde-${dia}`}>Desde:</Label>
                      <Select
                        id={`horaDesde-${dia}`}
                        value={formData.dias[dia].horaDesde}
                        onChange={(e) => handleHourChange(dia, "horaDesde", e.target.value)}
                      >
                        {horas.slice(0, -1).map((hora) => (
                          <option key={hora} value={hora}>
                            {hora}
                          </option>
                        ))}
                      </Select>

                      <Label htmlFor={`horaHasta-${dia}`}>Hasta:</Label>
                      <Select
                        id={`horaHasta-${dia}`}
                        value={formData.dias[dia].horaHasta}
                        onChange={(e) => handleHourChange(dia, "horaHasta", e.target.value)}
                      >
                        {horasHastaDisponibles.map((hora) => (
                          <option key={hora} value={hora}>
                            {hora}
                          </option>
                        ))}
                      </Select>
                      <Label htmlFor={`Cancha-${dia}`}>Hasta:</Label>
                      <Label htmlFor="cancha">Selecciona la Cancha:</Label>
                      <Select 
                        id="cancha"
                        name="cancha"
                        value={formData.dias[dia].cancha} // Usa el valor específico del día
                        onChange={(e) => handleCanchaChange(dia, e)}
                        required
                      >
                        <option value="">Selecciona una cancha</option>
                        {canchasDisponibles.map((c) => (
                          <option key={c._id} value={c.nombre}>{c.nombre}</option>
                        ))}
                      </Select>
                      <Label htmlFor="equipo">Selecciona tu Equipo:</Label>
                      <Select
                        id="equipo"
                        name="equipo"
                        value={formData.dias[dia].equipo} // Usa el valor específico del día
                        onChange={(e) => handleEquipoChange(dia, e)}
                        required
                        disabled={!formData.dias[dia].cancha} 
                      >
                        <option value="">Selecciona un equipo</option>
                        {equiposFiltrados.map((e) => (
                          <option key={e._id} value={e.nombre}>{e.nombre}</option>
                        ))}
                      </Select>
                    </FormGroup>
                  )}
                </div>
              ))}
            </FormGroup>
          </>
        )}

        <Button type="submit">Enviar Solicitud</Button>
      </form>
    </FormularioContainer>
  );
}

const FormularioContainer = styled.div`
