`;

const LabelCheckbox = styled.label`
  display: flex;
  align-items: center;
`;

const InputCheckbox = styled.input`
  margin-right: 10px;
`;

const LabelRadio = styled.label`
  display: block;
  margin-bottom: 5px;
`;

const InputRadio = styled.input`
  margin-right: 10px;
`;

const Fecha = styled.div`
  margin: 15px 0;
