import React, { useState, useEffect } from "react";
import styled from "styled-components";
import DatePicker from "react-datepicker"; 
import "react-datepicker/dist/react-datepicker.css"; 

function ReservaEspecial() {
  const [formData, setFormData] = useState({
    archivo: null,
    meses: {
      Enero: false,
      Febrero: false,
      Marzo: false,
      Abril: false,
      Mayo: false,
      Junio: false,
      Julio: false,
      Agosto: false,
      Septiembre: false,
      Octubre: false,
      Noviembre: false,
      Diciembre: false,
    },
    dias: {
      Lunes: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
      Martes: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
      Miércoles: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
      Jueves: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
      Viernes: { horaDesde: "08:00", horaHasta: "08:00", seleccionado: false },
    },
  });

  const [selectedDate, setSelectedDate] = useState(null);
  const [error, setError] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);
  const [mesesDisponibles, setMesesDisponibles] = useState([]);
  const [diaActual] = useState(new Date());
  const [reservarDiaEspecifico, setReservarDiaEspecifico] = useState(false);
  const [horaDesde, setHoraDesde] = useState("08:00");
  const [horaHasta, setHoraHasta] = useState("08:00");
  
  // Nuevo estado para las canchas y equipos disponibles
  const [canchasDisponibles, setCanchasDisponibles] = useState([]);
  const [equipoSeleccionado, setEquipoSeleccionado] = useState(null);

  const horas = [];
  for (let h = 8; h <= 20; h += 2) {
    const hour = h < 10 ? `0${h}:00` : `${h}:00`;
    horas.push(hour);
  }

  useEffect(() => {
    const mesActual = diaActual.getMonth(); 
    const semestreActual = Math.floor(mesActual / 6) + 1; 

    const meses = semestreActual === 1
      ? ["Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio"]
      : ["Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre"];

    const mesesFiltrados = meses.slice(mesActual % 6);
    const mesesSeleccionables = mesesFiltrados.map(mes => ({
      nombre: mes,
      seleccionado: false,
    }));

    setMesesDisponibles(mesesSeleccionables);
  }, [diaActual]);

  useEffect(() => {
    const fetchCanchasYEquipos = async () => {
      const token = localStorage.getItem('token');
      if (!token || !selectedDate || !horaDesde) {
        return;
      }

      try {
        const response = await fetch('http://localhost:5000/api/equipo_and_canchas', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
          body: JSON.stringify({ 
            fecha: selectedDate.toISOString().split('T')[0], 
            hora: horaDesde 
          }),
        });

        if (response.ok) {
          const data = await response.json();
          setCanchasDisponibles(data.canchas_disponibles);
        } else {
          const errorData = await response.json();
          setError(errorData.error || 'Error al obtener los datos.');
        }
      } catch (error) {
        console.error('Error de red:', error);
        setError('Error al conectar con el servidor.');
      }
    };

    fetchCanchasYEquipos();
  }, [selectedDate, horaDesde]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
    setError(""); 
  };

  const handleHourChange = (dia, tipo, valor) => {
    setFormData((prevState) => ({
      ...prevState,
      dias: {
        ...prevState.dias,
        [dia]: {
          ...prevState.dias[dia],
          [tipo]: valor,
        },
      },
    }));
  };

  const handleCheckboxChange = (dia) => {
    setFormData((prevState) => ({
      ...prevState,
      dias: {
        ...prevState.dias,
        [dia]: {
          ...prevState.dias[dia],
          seleccionado: !prevState.dias[dia].seleccionado,
        },
      },
    }));
  };

  const handleMonthChange = (mes) => {
    setFormData((prevState) => ({
      ...prevState,
      meses: {
        ...prevState.meses,
        [mes]: !prevState.meses[mes], 
      },
    }));
  };

  const toggleReservarDiaEspecifico = () => {
    setReservarDiaEspecifico(!reservarDiaEspecifico);
    setSelectedDate(null); 
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);

    const token = localStorage.getItem('token');
    const data = new FormData();
    
    // Verificar campos requeridos
    if (reservarDiaEspecifico && !selectedDate) {
      setError("Debes seleccionar una fecha.");
      return;
    }

    if (!reservarDiaEspecifico) {
      const mesesSeleccionados = Object.keys(formData.meses).filter(mes => formData.meses[mes]);
      const diasSeleccionados = Object.keys(formData.dias).filter(dia => formData.dias[dia].seleccionado);
      
      if (mesesSeleccionados.length === 0) {
        setError("Debes seleccionar al menos un mes.");
        return;
      }

      if (diasSeleccionados.length === 0) {
        setError("Debes seleccionar al menos un día.");
        return;
      }
    }

    // JSON para reserva específica
    if (reservarDiaEspecifico) {
      const fecha = selectedDate.toISOString().split('T')[0]; 
      const bloquesDeHoras = [{ dia: fecha, horaDesde, horaHasta }]; 

      const solicitudReservas = {
        fecha,
        bloquesDeHoras,
        cancha: equipoSeleccionado, // Agregar la cancha seleccionada
        estado: 'Pendiente',
      };

      data.append('solicitudReservas', JSON.stringify(solicitudReservas));
    } 
    // JSON para reserva general
    else {
      const mesesSeleccionados = Object.keys(formData.meses).filter(mes => formData.meses[mes]);
      const diasSeleccionados = Object.keys(formData.dias).filter(dia => formData.dias[dia].seleccionado);

      const solicitudReservas = {
        meses: mesesSeleccionados,
        dias: diasSeleccionados.map(dia => ({
          nombre: dia,
          horaDesde: formData.dias[dia].horaDesde,
          horaHasta: formData.dias[dia].horaHasta,
        })),
        cancha: equipoSeleccionado, // Agregar la cancha seleccionada
        estado: 'Pendiente',
      };

      data.append('solicitudReservas', JSON.stringify(solicitudReservas));
    }

    // Si hay un archivo, adjuntarlo
    if (!selectedFile) {
      setError("Debes subir un archivo.");
      return;
    } else {
      data.append('file', selectedFile);
    }

    try {
      const response = await fetch('http://localhost:5000/special_request', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: data,
      });

      if (response.ok) {
        alert("Tu solicitud ha sido enviada para su revisión.");
      } else {
        const result = await response.json();
        setError(result.error || 'Error al enviar la solicitud.');
      }
    } catch (error) {
      setError('Error al conectar con el servidor.');
    }
  };

  return (
    <FormularioContainer>
      <h2>Reserva Especial</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="file">Sube un archivo:</label>
          <input type="file" onChange={handleFileChange} />
        </div>
        <div>
          <label>Selecciona una fecha:</label>
          <DatePicker selected={selectedDate} onChange={date => setSelectedDate(date)} />
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <h3>Canchas Disponibles</h3>
        {canchasDisponibles.length > 0 ? (
          <select onChange={e => setEquipoSeleccionado(e.target.value)} value={equipoSeleccionado || ''}>
            <option value="">Selecciona una cancha</option>
            {canchasDisponibles.map((cancha, index) => (
              <option key={index} value={cancha.id}>{cancha.nombre}</option>
            ))}
          </select>
        ) : (
          <p>No hay canchas disponibles para la fecha y hora seleccionadas.</p>
        )}
        <h3>Reservar Día Específico</h3>
        <input type="checkbox" checked={reservarDiaEspecifico} onChange={toggleReservarDiaEspecifico} />
        <span>Reservar para un día específico</span>
        {reservarDiaEspecifico && (
          <div>
            <label>Hora Desde:</label>
            <select value={horaDesde} onChange={e => setHoraDesde(e.target.value)}>
              {horas.map(hora => (
                <option key={hora} value={hora}>{hora}</option>
              ))}
            </select>
            <label>Hora Hasta:</label>
            <select value={horaHasta} onChange={e => setHoraHasta(e.target.value)}>
              {horas.map(hora => (
                <option key={hora} value={hora}>{hora}</option>
              ))}
            </select>
          </div>
        )}
        <button type="submit">Enviar Solicitud</button>
      </form>
    </FormularioContainer>
  );
}

const FormularioContainer = styled.div`
  /* Aquí van tus estilos personalizados */
`;

export default ReservaEspecial;
