import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es'; 
registerLocale('es', es); 

function ReservayEquipo() {
  const navigate = useNavigate(); // Obtiene navigate para ser usado en los efectos

  // Función para obtener la próxima fecha hábil si hoy es fin de semana
  const obtenerProximaFechaHabil = () => {
    const hoy = new Date();
    const dia = hoy.getDay(); // 0 (Dom) a 6 (Sáb)

    if (dia === 6) { // Sábado
      hoy.setDate(hoy.getDate() + 2); // Mover a lunes
    } else if (dia === 0) { // Domingo
      hoy.setDate(hoy.getDate() + 1); // Mover a lunes
    }

    return hoy;
  };

  const calcularFechaMaxima = (fechaMinima) => {
    // Clonar la fecha mínima
    const fechaMax = new Date(fechaMinima);
    fechaMax.setMonth(fechaMax.getMonth() + 6); // Añadir 6 meses
  
    // Ajustar la fecha máxima si cae en fin de semana
    const diaMax = fechaMax.getDay();
    if (diaMax === 6) { // Sábado
      fechaMax.setDate(fechaMax.getDate() - 1); // Mover a viernes
    } else if (diaMax === 0) { // Domingo
      fechaMax.setDate(fechaMax.getDate() - 2); // Mover a viernes
    }
  
    // Ajustar la fecha mínima si cae en fin de semana
    const diaMin = fechaMinima.getDay();
    if (diaMin === 6) { // Sábado
      fechaMinima.setDate(fechaMinima.getDate() - 1); // Mover a viernes
    } else if (diaMin === 0) { // Domingo
      fechaMinima.setDate(fechaMinima.getDate() - 2); // Mover a viernes
    }
  
    return fechaMax;
  };
  

  // Calcular fechas mínimas y máximas
  const fechaMinima = obtenerProximaFechaHabil();
  const fechaMaxima = calcularFechaMaxima(fechaMinima);

  // Estado
  const [disp, setDis] = useState(null);
  const [selectedDate, setSelectedDate] = useState(fechaMinima);
  const [time, setTime] = useState(null);
  const [error, setError] = useState(null);
  const [cancha, setCancha] = useState('');
  const [canchaTipo, setCanchaTipo] = useState('');
  const [equipo, setEquipo] = useState('');
  const [canchasDisponibles, setCanchasDisponibles] = useState([]);
  const [equiposDisponibles, setEquiposDisponibles] = useState([]);

  // Efecto para obtener canchas y equipos disponibles
  useEffect(() => {
    const fetchCanchasYEquipos = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No se encontró el token de autenticación.');
        return;
      }

      try {
        const response = await fetch('http://localhost:5000/api/equipo_and_canchas', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
          body: JSON.stringify({ 
            fecha: selectedDate.toISOString().split('T')[0], 
            hora: time 
          }),
        });

        if (response.ok) {
          const data = await response.json();
          setCanchasDisponibles(data.canchas_disponibles);
          setEquiposDisponibles(data.equipos_disponibles);
        } else {
          const errorData = await response.json();
          setError(errorData.error || 'Error al obtener los datos.');
        }
      } catch (error) {
        console.error('Error de red:', error);
        setError('Error al conectar con el servidor.');
      }
    };

    // Solo fetch si hay una fecha y hora seleccionadas
    if (selectedDate && time) {
      fetchCanchasYEquipos();
    }
  }, [selectedDate, time]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);

    const token = localStorage.getItem('token');
    const formData = {
      fecha: selectedDate.toISOString().split('T')[0], // Formato YYYY-MM-DD
      hora: time,
      cancha: cancha,
      equipo: equipo,
    };

    try {
      const response = await fetch('http://localhost:5000/api/reservas', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify(formData),
      });

      if (response.status === 201) {
        alert("Se ha reservado con éxito");
        navigate("/TuReservacion"); // Se usa navigate aquí
      } else if (response.status === 409) {
        const result = await response.json();
        setError(result.error); // Mostrar mensaje de conflicto
      } else {
        setError('Ocurrió un error al realizar la reserva.');
      }
    } catch (error) {
      setError('Error al conectar con el servidor.');
    }
  };

  useEffect(() => {
    const verificarReservas = async () => {
      setError(null);
      const token = localStorage.getItem('token');
      const formData = {
        fecha: selectedDate.toISOString().split('T')[0], // Formato YYYY-MM-DD
      };

      try {
        const response = await fetch('http://localhost:5000/api/verificar_reservas', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
          body: JSON.stringify(formData),
        });

        if (response.ok) {
          // Si hay reservas, establece la disponibilidad
          setDis(true);
        } else if (response.status === 409) {
          const result = await response.json();
          setDis(false);
          setError(result.error);
        } else if (response.status === 410) {
          // Si hay exactamente dos reservas, realiza la acción correspondiente
          const result = await response.json();
          setDis(false);
          alert(result.error);
          navigate("/TuReservacion"); // Se usa navigate aquí
        }

      } catch (error) {
        setError('Error al conectar con el servidor.');
      }
    };

    // Solo llama a la función si hay una fecha seleccionada
    if (selectedDate) {
      verificarReservas();
    }
  }, [selectedDate, navigate]); // Agregando `navigate` como dependencia

  const generarOpcionesTiempo = () => {
    const opciones = [];
    for (let hora = 8; hora <= 18; hora += 2) {
      const horaFormateada = hora.toString().padStart(2, '0') + ':00';
      opciones.push(horaFormateada);
    }
    return opciones;
  };

  const esHoraPasada = (horaSeleccionada) => {
    const hoy = new Date();
    const fechaSeleccionada = selectedDate.toDateString() === hoy.toDateString();

    if (!fechaSeleccionada) return false;

    const [hora, minuto] = horaSeleccionada.split(':').map(Number);
    const ahora = new Date();
    return hora < ahora.getHours() || (hora === ahora.getHours() && minuto < ahora.getMinutes());
  };

  const handleCanchaChange = (e) => {
    const canchaNombre = e.target.value;
    setCancha(canchaNombre);

    const canchaSeleccionada = canchasDisponibles.find(c => c.nombre === canchaNombre);
    if (canchaSeleccionada) {
      setCanchaTipo(canchaSeleccionada.tipo);
    } else {
      setCanchaTipo('');
    }
  };

  const equiposFiltrados = canchaTipo
    ? equiposDisponibles.filter(equipo => equipo.tipo === canchaTipo)
    : equiposDisponibles;

  return (
    <Wrapper>
      <FormularioContainer>
        <h2>Reserva tu Hora y Equipo</h2>
        <form onSubmit={handleSubmit}>
          <Fecha>
            <Label>Selecciona el Día:</Label>
              <DatePickerWrapper>
                <DatePicker
                  selected={selectedDate}
                  onChange={(date) => setSelectedDate(date)}
                  inline
                  minDate={fechaMinima} // Inicio del semestre
                  maxDate={fechaMaxima} // Fin del semestre
                  filterDate={(date) => {
                    const day = date.getDay();
                    return day !== 0 && day !== 6; // Permitir solo lunes a viernes
                  }}
                  dateFormat="P" // Formato de fecha adaptado al locale
                  locale="es" // Establece el locale a español
                  required
                  showMonthDropdown={false}
                  showYearDropdown={false}
                  dropdownMode="select"
                />
              </DatePickerWrapper>
          </Fecha>
          <Hora>
            <Label htmlFor="hora">Selecciona la Hora:</Label>
            <Select
              id="hora"
              name="hora"
              value={time}
              onChange={(e) => setTime(e.target.value)}
              disabled={!disp}
              required
            >
              <option value="">Selecciona una hora</option>
              {generarOpcionesTiempo().map((hora) => (
                <option key={hora} value={hora} disabled={esHoraPasada(hora)}>
                  {hora}
                </option>
              ))}
            </Select>
          </Hora>
          <Cancha>
            <Label htmlFor="cancha">Selecciona la Cancha:</Label>
            <Select
              id="cancha"
              name="cancha"
              value={cancha}
              onChange={handleCanchaChange}
              required
              disabled={!time} // Deshabilitar si no hay una hora seleccionada
            >
              <option value="">Selecciona una cancha</option>
              {canchasDisponibles.map((c) => (
                <option key={c._id} value={c.nombre}>{c.nombre}</option>
              ))}
            </Select>
          </Cancha>
          <Equipo>
            <Label htmlFor="equipo">Selecciona tu Equipo:</Label>
            <Select
              id="equipo"
              name="equipo"
              value={equipo}
              onChange={(e) => setEquipo(e.target.value)}
              required
              disabled={!time || !canchaTipo} // Deshabilitar si no hay una hora seleccionada o tipo de cancha
            >
              <option value="">Selecciona un equipo</option>
              {equiposFiltrados.map((e) => (
                <option key={e._id} value={e.nombre}>{e.nombre}</option>
              ))}
            </Select>
          </Equipo>
          <Button type="submit">Reservar</Button>
        </form>
        {error && <ErrorNotification>{error}</ErrorNotification>}
      </FormularioContainer>
    </Wrapper>
  );
}

